  // padding-left:80px;

`;
const ProfileDiv = styled.div`
  width: 400px;
  height: 28px;
  // background:white;
  display: flex;
  padding-left: 50px;
`;
const UserDiv = styled.div`
  width: 120px;
  height: 26px;
  // border-right: solid #e6e6e6;
  :nth-child(2) {
    border-right: solid #e6e6e6;
    border-left: solid #e6e6e6;
  }

  padding-top: 2px;
  padding-left: 28px;
`;
const NavMenu = styled.ul`
  flex-direction: column;
  width: 100%;
  position: absolute;
  // top: 80px;
  // left:-100%;
  transition: all .5s ease;
`;
const NavActive = styled.ul`
  position: absolute;
  z-index: 1;

`;
const MenuLi = styled.li`
  text-align: center;
  padding-bottom: 40px;
  font-weight: 600;
  font-size: 20px;
  transition: all 0.25s ease;

  :hover {
    transform: scale(1.3, 1.3);
  }

  :first-child {
    padding-top: 20px;
  }

  a {
    color: black;
  }

  a:hover {
    color: black;
  }

`;
const MenuDiv = styled.div`
  width: 200px;
  flex-wrap: wrap;
  padding-top: 30px;
  min-width: 200px;
`;
const LogoDiv = styled.div`
  width: 260px;
  position: absolute;
  padding-top: 25px;
  min-height: 50px;
  // background:red;
`;
const MenuA = styled.div`
  width: 30px;
  padding-top: 90px;
  // minwidth:120px;
`;
const PlaceDiv = styled.div`
  position: fixed;
  width: 200px;
  // background:red;
  height: 30px;
  top: 200px;
  left: 1350px;
  right: 1485px;
  bottom: 219px;
  // border:solid;
  text-align: center;
  font-weight: 600;
  font-size: 20px;

  :hover {
    transform: scale(1.3, 1.3);
  }

  display: flex;
  flex-wrap: wrap;
`;

function Layout({children}) {
  const dispatch = useDispatch();
  const {me, local} = useSelector((state) => state.user);
  const [isOpen, setMenu] = useState(false);
  const [profile, SetProfile] = useState(false);
  const [select, SetSelect] = useState("");
  const [place, SetPlace] = useState(false);
  const [searchword,onSearchword,setSearchword] = useInput("");

  const PlaceClick = () => {
    SetPlace(true);
  }

  const toggleMenu = () => {
    setMenu(!isOpen);
  }

  const goProfile = () => {
    SetProfile(true);
    console.log(profile);
    Router.push('/profile');
  }

  // const onSearch = useCallback(() => {
  //   Router.push(`/hashtag/${searchInput}`);
  // }, [searchInput]);

  const onWrite = useCallback(() => {
    Router.push('/write');
  }, []);

  const onLogIn = useCallback(() => {
    Router.push('/loginpage');
  }, []);

  const onLogOut = useCallback(() => {
    dispatch(logoutRequestAction());
    Router.push('/');
  }, []);

  const onSearching = useCallback(() => {
    dispatch({
      type:UPDATE_SEARCH,
      data:{select:select,
      searchword:searchword,}
    });
    Router.push(`/search/${select}*${searchword}`);
  }, []);

  const menu = ['글내용','글제목','닉네임'];
  

  return (
    <div style={{width: "100%"}}>
      <div style={{position: "relative", width: "100%"}}>
        <GlobalStyle/>
        <div>
          <Topbar>
            <TopDiv>
              {!me ?
                (<div onClick={onLogIn}>로그인/회원가입</div>) :
                (<div onClick={onLogOut}>로그아웃</div>)}
              <div style={{paddingLeft: "20px"}}>
                내상점
              </div>
            </TopDiv>
          </Topbar>
          <NavBar>
            <NavBarDiv>
              <MenuDiv>
                <LogoDiv>
                  <Navbar.Brand href="/">
                    <img
                      alt=""
                      src={logo}
                      width="245px;"
                      height="45px"
                      className="d-inline-block align-top"
                      style={{paddingTop: "10px"}}
                    />
                  </Navbar.Brand>
                </LogoDiv>
                <MenuA style={{paddingBottom: "20px"}}>
                  {!isOpen ?
                    <MenuOutlined style={{fontSize: "20px"}} onClick={toggleMenu}/>
                    :
                    <CloseOutlined style={{fontSize: "20px"}} onClick={toggleMenu}/>
                  }
                  {isOpen ?
                    <NavActive>
                      {MenuItems.map((item, index) => {
                        return (
                          <>
                            {index === 0 &&
                            <Link href="/objectrecieve"><MenuLi key={index}><a>{item.title}</a></MenuLi></Link>}
                            {index === 1 &&
                            <Link href="/objectsend"><MenuLi key={index}><a>{item.title}</a></MenuLi></Link>}
                            {index === 2 &&
                            <Link href="/talentrecieve"><MenuLi key={index}><a>{item.title}</a></MenuLi></Link>}
                            {index === 3 &&
                            <Link href="/talentsend"><MenuLi key={index}><a>{item.title}</a></MenuLi></Link>}
                            {index === 4 &&
                            <Link href="/cooperate"><MenuLi key={index}><a>{item.title}</a></MenuLi></Link>}
                            {index === 5 &&
                            <Link href="/playground"><MenuLi key={index}><a>{item.title}</a></MenuLi></Link>}
                          </>
                        )
                      })}
                    </NavActive> : <NavMenu/>
                  }
                </MenuA>
              </MenuDiv>
              <div style={{paddingLeft: "160px"}}>
                <SelcectDiv>
                  <SelectDropD>
                    <Select
                      style={{width: 120}}
                      defaultValue={menu[0]}
                      onChange={onCategory}
                    >
                      {menu.map((city) => (
                        <Option value={city}>{city}</Option>
                      ))}
                    </Select>
                  </SelectDropD>
                  {/*<Select placeholder={"지역, 상품명 입력"} value={searchword} onChange={onSearchword}/>*/}
                  <SearchInput placeholder={"지역, 상품명 입력"} value={searchword} onChange={onSearchword} onSearch={onSearching}/>
                  <div style={{paddingTop: "3px", paddingLeft: "115px"}}>
                    <SearchOutlined onClick={onSearching}/>
                  </div>
                </SelcectDiv>
              </div>
              <ProfileDiv>
                <UserDiv onClick={goProfile}>
                  <UserOutlined/> 내프로필
                </UserDiv>
                <UserDiv>
                  <MailOutlined/> 알림톡
                </UserDiv>
                <UserDiv onClick={onWrite}>
                  <FormOutlined/> 글 쓰기
                </UserDiv>
              </ProfileDiv>
            </NavBarDiv>
          </NavBar>
        </div>
        <div style={{marginTop: 0, zIndex: 5 , width: '90%'}}>
          <Row gutter={8}>
            <Col xs={7} md={7}/>
            <Col xs={11} md={11}>
              {children}
            </Col>
            <Col xs={6} md={6}/>
          </Row>
        </div>
        <PlaceDiv onClick={PlaceClick}>
          <div style={{paddingLeft: "4px"}}>
            <AimOutlined style={{paddingRight: "10px"}}/>
            동네 설정
          </div>
          <SearchLocation/>
          {local ?
            <div style={{color: "#15254d", fontSize: "25px", width: "114px", paddingRight: ""}}>
              {local}
            </div> :
            <div>
            </div>
          }
        </PlaceDiv>
      </div>
    </div>
  )
}

export default Layout;
